import Reactr from "react";
import styled, { keyframes } from "styled-components";

const NavItems = () => {
  state = {
    navTitle: [
      {
        id: 1,
        text: "Home",
      },
      {
        id: 2,
        text: "About",
      },
      {
        id: 3,
        text: "Work",
      },
      {
        id: 4,
        text: "Projects",
      },
    ],
  };

  function animationRender() {
    const navItems = this.state.navTitle;

    return navItems.map((navItem, index) => {
      console.log(index);

      var navLinkFade = keyframes`
                0% { opacity: 0; transform: translate(50px); }
                100% { opacity: 1; transform: translateX(0px); }
            `;
      const NavListItem = styled.li`
        animation-name: ${navLinkFade};
        animation-duration: "0.5s";
        animation-delay: ${index / 7 + 0.3}s;
        animation-direction: "forwards";
        animation-timing-function: "ease";
      `;

      return (
        <NavListItem className="navLinksLi" key={index}>
          {navItem.text}
        </NavListItem>
      );
    });
  }
};

export default NavItems;
