import css from "dom-css";
import React, { Component } from "react";
import PropTypes from "prop-types";
import { Scrollbars } from "react-custom-scrollbars";

import "./ScrollBar.css";

class ShadowScrollbars extends Component {
  constructor(props, ...rest) {
    super(props, ...rest);
    this.state = {
      scrollTop: 0,
      scrollHeight: 0,
      clientHeight: 0,
      top: 0,
    };

    this.handleUpdate = this.handleUpdate.bind(this);
    this.renderView = this.renderView.bind(this);
    this.renderThumb = this.renderThumb.bind(this);
  }

  handleUpdate(values) {
    const { shadowTop, shadowBottom } = this.refs;
    const { scrollTop, scrollHeight, clientHeight } = values;
    const shadowTopOpacity = (1 / 20) * Math.min(scrollTop, 20);
    const bottomScrollTop = scrollHeight - clientHeight;
    const shadowBottomOpacity =
      (1 / 20) * (bottomScrollTop - Math.max(scrollTop, bottomScrollTop - 20));
    css(shadowTop, { opacity: shadowTopOpacity });
    css(shadowBottom, { opacity: shadowBottomOpacity });
    const { top } = values;
    this.setState({ top });
  }

  renderView({ style, ...props }) {
    const viewStyle = {
      padding: 15,
      backgroundColor: "#72D095",
    };
    return (
      <div className="box" style={{ ...style, ...viewStyle }} {...props} />
    );
  }

  renderThumb({ style, ...props }) {
    const { top } = this.state;
    const thumbStyle = {
      // backgroundColor: `rgb(${Math.round(255 - top * 255)}, 255, ${Math.round(
      //   255 - top * 255
      // )})`,
      position: "relative",
      display: "block",
      width: "100%",
      cursor: "pointer",
      borderRadius: "inherit",
      backgroundColor: "rgba(123, 154, 255, 0.418)",
    };

    return (
      <div
        style={{ ...style, thumbStyle }}
        {...props}
        className="thumb-vertical"
      />
    );
  }

  render() {
    const { style, ...props } = this.props;
    const containerStyle = {
      ...style,
      position: "relative",
    };
    const shadowTopStyle = {
      position: "absolute",
      top: 0,
      left: 0,
      right: 0,
      height: 10,
      background:
        "linear-gradient(to bottom, rgba(0, 0, 0, 0.2) 0%, rgba(0, 0, 0, 0) 100%)",
    };
    const shadowBottomStyle = {
      position: "absolute",
      bottom: 0,
      left: 0,
      right: 0,
      height: 10,
      background:
        "linear-gradient(to top, rgba(0, 0, 0, 0.2) 0%, rgba(0, 0, 0, 0) 100%)",
    };
    return (
      <div style={containerStyle}>
        <Scrollbars
          ref="scrollbars"
          renderView={this.renderView}
          renderTrackVertical={(props) => (
            <div {...props} className="track-vertical" />
          )}
          renderThumbVertical={this.renderThumb}
          onUpdate={this.handleUpdate}
          {...props}
        />
        <div ref="shadowTop" style={shadowTopStyle} />
        <div ref="shadowBottom" style={shadowBottomStyle} />
      </div>
    );
  }
}

ShadowScrollbars.propTypes = {
  style: PropTypes.object,
};

export default ShadowScrollbars;
