import React from "react";
import style from "./recipe.module.css";
import Card from "./Card";

const Recipe = ({ title, calories, image, ingredients }) => {
  function animateFront(e) {
    e.target.style.transform = "perspective(600px) rotateY(-180deg)";
  }

  function animateBack(e) {
    e.target.style.transform = "perspective(600px) rotateY(0deg)";
  }

  return (
    <Card className={style.recipe}>
      <div className={style.front} onMouseOver={animateFront}>
        <div className={style.frontContent + " " + style.center}>
          <h1>{title}</h1>
          <img src={image} alt={title} />
        </div>
      </div>

      <div>
        <div
          className={
            style.recipeContent + " " + style.back + " " + style.center
          }
          onMouseOver={animateBack}
        >
          <h3>Ingredients</h3>
          <ol>
            {ingredients.map((ingredient, i) => {
              return <li key={[i, "=", ingredient]}>{ingredient.text}</li>;
            })}
          </ol>
          <p>
            Calories: <strong>{calories}</strong> cal
          </p>
        </div>
      </div>
    </Card>
  );
};

export default Recipe;
