import React, { useState } from "react";
import style from "./Nav.module.css";

const Nav = () => {
  const [isOpened, setIsOpened] = useState(false);
  const navLinks = document.querySelectorAll(".navLinksA");

  function navSlide(e) {
    // e.preventDefault();

    if (isOpened) {
      setIsOpened(false);
    } else {
      setIsOpened(true);
      navLinks.forEach((link, index) => {
        console.log(index);
        console.log(index / 7 + 0.3);
        link.style = {
          animation: `${style.navLinkFade} 0.5s ease forwards {
            index / 7 + 0.3
          }s`,
        };
      });
    }
  }

  return (
    <nav>
      <div className={style.logo}>
        <h4>Ingredients</h4>
      </div>

      <ul
        className={
          isOpened === true
            ? `${style.navLinks} ${style.navActive}`
            : style.navLinks
        }
      >
        <li className="navLinksA">
          <a href="#">Home</a>
        </li>
        <li className="navLinksA">
          <a href="#">About</a>
        </li>
        <li className="navLinksA">
          <a href="#">Work</a>
        </li>
        <li className="navLinksA">
          <a href="#">Projects</a>
        </li>
      </ul>

      <div className={style.burger} onClick={navSlide}>
        <div className={style.line1}></div>
        <div className={style.line2}></div>
        <div className={style.line3}></div>
      </div>
    </nav>
  );
};

export default Nav;
