import React, { useState } from "react";
import style from "./Nav.module.css";
import NavItems from "./NavItems";

const Nav = () => {
  const isMobile = window.innerWidth < 768;
  console.log(isMobile);

  const navItems = [
    { id: 1, name: "Home" },
    { id: 2, name: "About" },
    { id: 3, name: "Content" },
    { id: 4, name: "Contact" },
  ];

  const [isOpened, setIsOpened] = useState(false);

  function navSlide(e) {
    e.preventDefault();

    if (isOpened) {
      setIsOpened(false);
    } else {
      setIsOpened(true);
    }
  }

  function printLi(isMobile) {
    let returnElement = null;
    navItems.forEach((navItem) => {
      console.log(navItem.name);
      console.log(navItem.id);
      console.log("Printed");

      if (isMobile === true) {
        returnElement = <NavItems name={navItem.name} index={navItem.id} />;
      } else {
        returnElement = <li key={navItem.id}> {navItem.name}</li>;
      }
      return returnElement;
    });
  }

  return (
    <nav>
      <div className={style.logo}>
        <h4>Ingredients</h4>
      </div>

      <ul
        className={
          isOpened === true
            ? `${style.navLinks} ${style.navActive}`
            : style.navLinks
        }
      >
        {printLi(isMobile)}
      </ul>

      <div className={style.burger} onClick={navSlide}>
        <div className={style.line1}></div>
        <div className={style.line2}></div>
        <div className={style.line3}></div>
      </div>
    </nav>
  );
};

export default Nav;
