import React from "react";
import style from "./recipe.module.css";
import Card from "./Card";

const Recipe = ({ title, calories, image, ingredients }) => {
  function animateFront(e) {}

  function animateBack(e) {}

  function animate(e) {
    if (
      e.target.classList.contains(style.front) ||
      e.target.style.transform === "rotateY(0deg)"
    ) {
      e.target.style.transform = "rotateY(-180deg)";
    } else if (
      e.target.classList.contains(style.front) ||
      e.target.style.transform === "rotateY(180deg)" ||
      e.target.style.transform === "rotateY(-180deg)"
    ) {
      e.target.style.transform = "rotateY(0deg)";
    }
  }

  return (
    <div className={style.recipe}>
      <Card>
        <div className={style.front} onMouseOver={animateFront}>
          <div className={style.frontContent}>
            <h1>{title}</h1>
            <img src={image} alt={title} />
          </div>
        </div>
        <div className={style.back} onMouseOver={animateBack}>
          <div>
            <div className={style.recipeContent}>
              <h3>Ingredients</h3>
              <ol>
                {ingredients.map((ingredient, i) => {
                  return <li key={[i, "=", ingredient]}>{ingredient.text}</li>;
                })}
              </ol>
              <p>
                Calories: <strong>{calories}</strong> cal
              </p>
            </div>
          </div>
        </div>
      </Card>
    </div>
  );
};

export default Recipe;
