import React, { useState } from "react";
import style from "./recipe.module.css";
import Card from "./Card";
import ReactCardFlip from "react-card-flip";

const Recipe = ({ title, calories, image, ingredients }) => {
  const [isFlipped, setIsFlipped] = useState(false);

  const handleClick = () => {
    setIsFlipped(!isFlipped);
  };

  // if (
  //   e.target.style.transform === "perspective(600px) rotateY(0deg)" ||
  //   e.target.classList.contains(style.front)
  // ) {
  //   $(e.target).css("transform", "perspective(600px) rotateY(-180deg)");
  //   $(e.target).css("boxShadow", "0px 5px 20px rgb(71, 71, 71)");
  // } else if (
  //   e.target.classList.contains(style.back) ||
  //   e.target.style.transform === "perspective(600px) rotateY(180deg)" ||
  //   e.target.style.transform === "perspective(600px) rotateY(-180deg)"
  // ) {
  //   e.target.style.transform = "perspective(600px) rotateY(0deg)";
  // }

  return (
    <ReactCardFlip
      className={style.recipe}
      isFlipped={isFlipped}
      flipDirection="vertical"
    >
      <Card className={style.front} onMouseOver={handleClick}>
        <div className={style.frontContent}>
          <h1>{title}</h1>
          <img src={image} alt={title} />
        </div>
      </Card>

      <Card className={style.back} onMouseOver={handleClick}>
        <div className={style.recipeContent}>
          <h3>Ingredients</h3>
          <ol>
            {ingredients.map((ingredient, i) => {
              return <li key={[i, "=", ingredient]}>{ingredient.text}</li>;
            })}
          </ol>
          <p>
            Calories: <strong>{calories}</strong> cal
          </p>
        </div>
      </Card>
    </ReactCardFlip>
  );
};

export default Recipe;
