import React, { useState } from "react";
import style from "./Nav.module.css";
import styled, { css, keyframes } from "styled-components";

const Nav = () => {
  const [isOpened, setIsOpened] = useState(false);
  const navLinks = document.querySelectorAll(".navLinksA");

  function navSlide(e) {
    // e.preventDefault();

    if (isOpened) {
      setIsOpened(false);
    } else {
      setIsOpened(true);
      let time = null;

      var spin = keyframes`
      0% { opacity: 0;transform: translate(50px); }
      100% { opacity: 1;transform: translateX(0px) }
      `;
      navLinks.forEach((link, index) => {
        console.log(index);
        console.log(link);
        time = index / 7 + 0.3;
        link.setAttribute(style, {
          animation: `${spin} 0.5s ease forwards ${time}s`,
        });
        // animationName: `${style.navLinkFade}`,
        // animationTimingFunction: "ease",
        // animationDuration: "0.5s",
        // animationDelay: `${time}s`,
        // animationFillMode: "forwards",
      });
    }
  }

  return (
    <nav>
      <div className={style.logo}>
        <h4>Ingredients</h4>
      </div>

      <ul
        className={
          isOpened === true
            ? `${style.navLinks} ${style.navActive}`
            : style.navLinks
        }
      >
        <li className="navLinksA">
          <a href="#">Home</a>
        </li>
        <li className="navLinksA">
          <a href="#">About</a>
        </li>
        <li className="navLinksA">
          <a href="#">Work</a>
        </li>
        <li className="navLinksA">
          <a href="#">Projects</a>
        </li>
      </ul>

      <div className={style.burger} onClick={navSlide}>
        <div className={style.line1}></div>
        <div className={style.line2}></div>
        <div className={style.line3}></div>
      </div>
    </nav>
  );
};

export default Nav;
