import styled from "styled-components";

import Card from "./Card";

export const TriggerFront = styled.div`
  &:hover ${Card} {
    transform: translate(200px, 150px) rotate(20deg);
  }
`;

export const TriggerBack = styled.div`
  border: 20px solid #999;

  &:hover ${Card} {
    transform: translate(200px, 150px) rotate(20deg);
  }
`;
