import React, { useEffect, useState } from "react";

import PopoverStickOnHover from "./PopOver";
import FormikForm from "./FormikForm";
import Modal from "react-modal";

import {
  TextField,
  Button,
  Checkbox,
  Radio,
  FormControlLabel,
  Select,
  MenuItem,
} from "@material-ui/core";

import { Form } from "react-bootstrap";
Modal.setAppElement("#root");

const AdvancedSearchModal = ({
  healthLabels,
  dietLabels,
  cuisineTypes,
  dishTypes,
  modalIsOpen,
  getAdvancedSearch,
  setModalIsOpen,
  search,
}) => {
  return (
    <Modal
      isOpen={modalIsOpen}
      onRequestClose={() => setModalIsOpen(false)}
      style={{
        overlay: { backgroundColor: "rgba(0,0,0,0.7)" },
      }}
    >
      <div className="ModalHeader" style={{ display: "flex" }}>
        <h2>Advanced Search</h2>
      </div>
      <div className="ModalContent">
        {/* <FormikForm /> */}
        {/* // TODO Change to Formik and MUI for better UI */}
        <Form onSubmit={getAdvancedSearch} className="advanced-search-form">
          {/* Search Starts Here */}
          <Form.Group controlId="Search">
            <Form.Label>Search: </Form.Label>
            <Form.Control type="text" placeholder="Chicken" value={search} />
          </Form.Group>
          {/* Search Ends Here */}
          {/* No. of Items Starts Here */}
          <Form.Group controlId="NoOfItems">
            <Form.Label>Number of Items Per Page</Form.Label>
            <Form.Control as="select">
              <option>10</option>
              <option>15</option>
              <option>20</option>
              <option>25</option>
              <option>30</option>
            </Form.Control>
          </Form.Group>
          {/* No. of Items Ends Here */}
          {/* Meal Type Starts Here */}
          <Form.Group controlId="MealType">
            <Form.Label>Meal Type: </Form.Label>
            <Form.Control as="select">
              <option>Breakfast</option>
              <option>Lunch</option>
              <option>Dinner</option>
              <option>Snack</option>
            </Form.Control>
          </Form.Group>
          <Form.Group controlId="DishType">
            <Form.Label>Dish Type</Form.Label>
            <div className="mb-3">
              {dishTypes.map((dishType, index) => (
                <div style={{ display: "inline-flex" }}>
                  <Form.Check
                    inline
                    label={`${dishType} `}
                    type="checkbox"
                    id={`inline-${dishType}-${index}`}
                  />
                </div>
              ))}
            </div>
          </Form.Group>
          {/* Meal Type Ends Here */}
          {/* Cuisine Type Starts Here */}
          <Form.Group controlId="CuisineType">
            <Form.Label>Cuisine Types</Form.Label>
            <Form.Control as="select">
              {cuisineTypes.map((cuisineType) => (
                <option>{cuisineType}</option>
              ))}
            </Form.Control>
          </Form.Group>
          {/* CuisineType Ends Here */}
          {/* Diet Starts Here */}
          <fieldset>
            <Form.Group controlId="Diet">
              <Form.Label>Diet</Form.Label>
              <div className="mb-3">
                {dietLabels.map((dietLabel, index) => (
                  <PopoverStickOnHover
                    component={<div>{`${dietLabel.description}`}</div>}
                    placement="top"
                    onMouseEnter={() => {}}
                    delay={200}
                  >
                    <div style={{ display: "inline-flex" }}>
                      <Form.Check
                        inline
                        label={`${dietLabel.name} `}
                        type="radio"
                        name="formHorizontalRadios"
                        id={`inline-${dietLabel.name}-${index}`}
                      />
                    </div>
                  </PopoverStickOnHover>
                ))}
              </div>
            </Form.Group>
          </fieldset>
          {/* Diet Ends Here */}
          {/* Health Labels Starts Here */}
          <Form.Group controlId="HealthLabel">
            <Form.Label>Health Labels</Form.Label>
            <div className="mb-3">
              {healthLabels.map((healthLabel, index) => (
                <PopoverStickOnHover
                  component={<div>{`${healthLabel.description}`}</div>}
                  placement="top"
                  onMouseEnter={() => {}}
                  delay={200}
                >
                  <div style={{ display: "inline-flex" }}>
                    <Form.Check
                      inline
                      label={`${healthLabel.name} `}
                      type="checkbox"
                      id={`inline-${healthLabel.name}-${index}`}
                    />
                  </div>
                </PopoverStickOnHover>
              ))}
            </div>
          </Form.Group>
          {/* Health Label Ends here */}
          <Button variant="success" type="submit">
            Submit
          </Button>
        </Form>
      </div>

      <div>
        <button onClick={() => setModalIsOpen(false)}>Close</button>
      </div>
    </Modal>
  );
};

export default AdvancedSearchModal;
