import React from "react";
import { Formik, Field, Form } from "formik";
import { TextField, Button, Checkbox } from "@material-ui/core";

const FormikForm = () => {
  return (
    <div>
      <Formik
        initialValues={{ search: "", isTall: false, cookies: [], yogurt: "" }}
        onSubmit={(data, { setSubmitting, resetForm }) => {
          setSubmitting(true);
          // *make async call
          console.log("Submit: ", data);
          setSubmitting(false);
          resetForm(true);
        }}
      >
        {({ values, isSubmitting, handleChange, handleBlur, handleSubmit }) => (
          <Form>
            {/* A simpler way of using text field */}
            <Field
              placeholder="Chicken"
              name="search"
              type="input"
              as={TextField}
            />
            {/* with the same name, the text will sync */}
            <TextField
              name="search"
              value={values.search}
              onChange={handleChange}
              onBlur={handleBlur}
            />
            {/* Checkbox */}
            <Field name="isTall" type="checkbox" as={Checkbox}></Field>
            {/* multiple checkboxes starts here */}
            <div>Cookies: </div>
            <Field
              name="cookies"
              type="checkbox"
              value="chocolate chip"
              as={Checkbox}
            ></Field>
            <Field
              name="cookies"
              type="checkbox"
              value="snicker doodle"
              as={Checkbox}
            ></Field>
            <Field
              name="cookies"
              type="checkbox"
              value="sugar"
              as={Checkbox}
            ></Field>
            {/* multiple checkboxes ends here */}

            {/* Button */}
            <div>
              <Button disabled={isSubmitting} type="submit">
                Submit
              </Button>
            </div>
            <pre>{JSON.stringify(values, null, 2)}</pre>
          </Form>
        )}
      </Formik>
    </div>
  );
};

export default FormikForm;
