import React from "react";
import style from "./Recipe.module.css";
import Card from "./Card";
import ShadowScrollBars from "./CustomScrollBars";
import "./ScrollBar.css";
import { Scrollbars } from "react-custom-scrollbars";

import Flippy, { FrontSide, BackSide } from "react-flippy";

const Recipe = ({ title, calories, image, ingredients }) => {
  return (
    <div className={style.recipe}>
      <Flippy
        className={style.card}
        flipOnHover={false} // default false
        flipOnClick={true} // default false
        flipDirection="horizontal" // horizontal or vertical
        // ref={(r) => (this.flippy = r)} // to use toggle method like this.flippy.toggle()
        // if you pass isFlipped prop component will be controlled component.
        // and other props, which will go to div
        /// these are optional style, it is not necessary
        // *style={{ zIndex: 0 }}
      >
        <FrontSide className={style.front} elementType={Card}>
          <div className={style.frontContent}>
            <h1>{title}</h1>
            <img src={image} alt={title} />
          </div>
        </FrontSide>

        <BackSide className={`${style.back} ${style.card}`}>
          <div className={style.recipeContent}>
            <h3>Ingredients</h3>
            <CustomScrollBars
              autoHide
              renderTrackVertical={(props) => (
                <div {...props} className="track-vertical" />
              )}
              renderThumbVertical={(props) => (
                <div {...props} className="thumb-vertical" />
              )}
              renderView={(props) => <div {...props} className="view" />}
              style={{
                height: "80%",
                width: "90%",
                marginLeft: "auto",
                marginRight: "auto",
              }}
            >
              <ol>
                {ingredients.map((ingredient, i) => {
                  return <li key={[i, "-", ingredient]}>{ingredient.text}</li>;
                })}
              </ol>
            </CustomScrollBars>
            <p>
              Calories: <strong>{calories}</strong> cal
            </p>
          </div>
        </BackSide>
      </Flippy>
    </div>
  );
};

export default Recipe;
