import React from "react";
import style from "./recipe.module.css";
import Card from "./Card";
import { TriggerFront, TriggerBack } from "./Trigger";

const Recipe = ({ title, calories, image, ingredients }) => {
  var animationFront = {
    transform: "perspective(600px) rotateY(0deg)",
    "&:hover": {
      transform: "perspective(600px) rotateY(-180deg)",
    },
  };

  var animationBack = {
    background: "#f1f1f1",
    transform: "perspective(600px) rotateY(1800deg)",
    "&:hover": {
      transform: "perspective(600px) rotateY(0deg)",
    },
  };

  function animateFront(e) {
    e.target.style.transform = "perspective(600px) rotateY(-180deg)";
  }

  function animateBack(e) {
    e.target.style.transform = "perspective(600px) rotateY(0deg)";
  }
  return (
    // <div className={style.card}>

    <div className={style.recipe}>
      <Card>
        <div
          className={(style.center, style.frontContent, style.front)}
          onMouseOver={animateFront}
        >
          <h1>{title}</h1>
          <img src={image} alt={title} />
        </div>

        <div
          className={(style.center, style.recipeContent)}
          style={animationBack}
        >
          <h3>Ingredients</h3>
          <ol>
            {ingredients.map((ingredient, i) => {
              return <li key={[i, "=", ingredient]}>{ingredient.text}</li>;
            })}
          </ol>
          <p>
            Calories: <strong>{calories}</strong> cal
          </p>
        </div>
      </Card>
    </div>
  );
};

export default Recipe;
