import React from "react";
import style from "./recipe.module.css";
import Card from "./Card";
import { TriggerFront, TriggerBack } from "./Trigger";

const Recipe = ({ title, calories, image, ingredients }) => {
  return (
    // <div className={style.card}>

    <div className={style.recipe}>
      <Card>
        <Card className={style.front}>
          <div className={(style.center, style.frontContent)}>
            <h1>{title}</h1>
            <img src={image} alt={title} />
          </div>
        </Card>

        <Card className={style.back}>
          <div className={(style.center, style.recipeContent)}>
            <h3>Ingredients</h3>
            <ol>
              {ingredients.map((ingredient, i) => {
                return <li key={[i, "=", ingredient]}>{ingredient.text}</li>;
              })}
            </ol>
            <p>
              Calories: <strong>{calories}</strong> cal
            </p>
          </div>
        </Card>
      </Card>
    </div>
  );
};

export default Recipe;
